import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Grid from '@mui/material/Unstable_Grid2'; // Grid version 2
import Button from '@mui/material/Button';
import { Pokemon } from './model/Pokemon';
import axios from 'axios';


function App() {
  const [pokemonId,setPokemonId]=useState("")
  const [pokemon,setPokemon]=useState<Pokemon>()
  function changePokemonId(id:string){
    setPokemonId(pokemonId+id)
  }

  function searchPokemonById(){
    console.log("Buscando pokemon con id: "+pokemonId);
    axios.get<Pokemon>("https://pokeapi.co/api/v2/pokemon/"+pokemonId).then((response:any)=>{
      setPokemon(response.data)
    });
  }

  return (
    <div className="App">
      <div className='Pokedex_img_container'>
          <div className='Pokedex_img_imagen'>
            <img src={pokemon?.sprites.other["official-artwork"].front_default}></img>
          </div>
          <div className='Pokedex_img_info'>
            <h2>Nombre: {pokemon?.name}</h2>
            <h2>ID: {pokemon?.id}</h2>
          </div>
      </div>
      <div className="Pokedex_buttons_container">
        <div className='keyboard'></div>
        <button className='btns_general' onClick={()=>changePokemonId("2")}>Hola mundo</button>

      </div>
    </div>
  );
}

export default App;
